import React, { useState } from "react";
import DateFnsUtils from "@date-io/date-fns";
import {
  MuiPickersUtilsProvider,
  KeyboardTimePicker,
  KeyboardDatePicker,
} from "@material-ui/pickers";

export const Home = () => {
  const [foodType, setFoodType] = useState("seeds");

  const [selectedDate, setSelectedDate] = useState(new Date());

  const handleDateChange = (date) => {
    setSelectedDate(date);
  };

  return (
    <section className="feeding-info">
      <div className="feeding-info-container">
        <h1 className="feeding-info-container__header">Form</h1>
        <form
          className="feeding-info-container__form"
          //   onSubmit={handleRegister}
        >
          <label className="feeding-info-container__form--time" for="time">
            What time the ducks are fed?
          </label>
          <MuiPickersUtilsProvider
            utils={DateFnsUtils}
            className="feeding-info-container__form--time-input"
            type="date"
            id="time"
            name="time"
            required
          >
            {" "}
            <KeyboardDatePicker
              margin="normal"
              id="date-picker-dialog"
              format="MM/dd/yyyy"
              value={selectedDate}
              onChange={handleDateChange}
              KeyboardButtonProps={{
                "aria-label": "change date",
              }}
            />
            <KeyboardTimePicker
              margin="normal"
              id="time-picker"
              value={selectedDate}
              onChange={handleDateChange}
              KeyboardButtonProps={{
                "aria-label": "change time",
              }}
            />
          </MuiPickersUtilsProvider>
          <label className="feeding-info-container__form--food" for="food">
            What food the ducks are fed?
          </label>
          <select
            className="feeding-info-container__form--food-select"
            id="food"
            name="food"
            defaultValue={foodType}
            required
            onChange={(e) => {
              const selectedFoodType = e.target.value;
              setFoodType(selectedFoodType);
            }}
          >
            <option value="seeds">Seeds</option>
            <option value="corn">Corn</option>
            <option value="oats">Oats</option>
          </select>
          <button
            className="feeding-info-container__form--register"
            type="submit"
          >
            Submit
          </button>
        </form>
      </div>
    </section>
  );
};
