import React, { useState, useEffect } from "react";
import axios from "axios";
import moment from "moment";

const { REACT_APP_BACKEND_URL, REACT_APP_PORT } = process.env;

export const Feeding = (props) => {
  const [selectedFeeding, setSelectedFeeding] = useState({});

  useEffect(() => {
    axios({
      method: "get",
      url: `${REACT_APP_BACKEND_URL}:${REACT_APP_PORT}/data`,
      headers: {
        "auth-token": `${sessionStorage.getItem("authToken")}`,
        "Access-Control-Allow-Origin": "*",
      },
    }).then((res) => {
      const feeding = res.data.find((feeding) => {
        return feeding._id === props.match.params.id;
      });
      setSelectedFeeding(feeding);
    });
  });

  return (
    <section className="feeding">
      <div className="feeding-container">
        <h3 className="feeding-container__header">Feeding Info</h3>
        <div className="feeding-container__feedinginfo">
          <h4 className="feeding-container__feedinginfo--date">
            Time of feeding :
          </h4>
          <span className="feeding-container__feedinginfo--date">
            {moment(`${selectedFeeding.time}`).format("MMMM Do, YYYY, LT")}
          </span>

          <span className="feeding-container__feedinginfo--location">
            Location : {selectedFeeding.location}
          </span>
          <span className="feeding-container__feedinginfo--type">
            Food Type : {selectedFeeding.foodType}
          </span>
          <span className="feeding-container__feedinginfo--amount">
            Amount of food : {selectedFeeding.amount} grams
          </span>
          <span className="feeding-container__feedinginfo--number">
            Number of ducks fed : {selectedFeeding.number}
          </span>
        </div>
      </div>
    </section>
  );
};
